rpc:DispatchHandler.contextX:
[#api]
{
  type:~[@social:Contact],
  out:~[#{[api].type.@nil}]
          { [.]
            {{
              id:=..id,
              knownAs:=..knownAs,
              attentionName:=..attentionName,
              organizationName:=..organizationName,
              email:=..email,
              phone:=..phone,
              postalAddress:=..postalAddress
                {{
                  streetAddress:=..streetAddress,
                  locator:=..locator,
                  locality:=..locality,
                  region:=..region,
                  postalCode:=..postalCode,
                }},
            }}
         }

}

rpc:JsonHandler.result:
![rpc:Call].request.isPost()
?[api].out([result].data)           
:@{[result].data
     =[result].data
      { [result].new
        ?.[*dt:Edit{ action:=`
           @{ 
              knownAs=[p].knownAs,
              attentionName=[p].attentionName,
              organizationName=[p].organizationName,
              email=[p].email,
              phone=[p].phone,
              postalAddress=postalAddress.[*dt:Edit].().@log("Postal original "+.)
              {
                (.==null?[@postal:Address.buffer].():.)
                {
                  .streetAddress=[p].postalAddress.streetAddress,
                  .locator=[p].postalAddress.locator,
                  .locality=[p].postalAddress.locality,
                  .region=[p].postalAddress.region,
                  .postalCode=[p].postalAddress.postalCode,
                  .
                }
              }.@log("PostalAddress "+.),
           }`
         }].()
        :.[*dt:Edit{ action:=`
           @{ 
              knownAs=[p].knownAs,
              attentionName=[p].attentionName,
              organizationName=[p].organizationName,
              email=[p].email,
              phone=[p].phone,
              postalAddress=postalAddress.[*dt:Edit].().@log("Postal original "+.)
              {
                (.==null?[@postal:Address.buffer].():.)
                {
                  .streetAddress=[p].postalAddress.streetAddress,
                  .locator=[p].postalAddress.locator,
                  .locality=[p].postalAddress.locality,
                  .region=[p].postalAddress.region,
                  .postalCode=[p].postalAddress.postalCode,
                  .
                }
              }.@log("PostalAddress "+.),
           }`
         }].()
       }
  ,[api].out([result].data) 
  }
