rpc:DispatchHandler.contextX:
[#api]
{
  type:~[@biz:ContactNote],
  out:~[#{[api].type.@nil}]
          { [.]
            {{ 
              id:=..id,
              entityId:=..entityId,
              creatorId:=..creatorId,
              creator:=[@swm:User].@forId(..creatorId)
              {{
                commonName:=..commonName,
              }},
              lastModified:=..lastModified,
              sourceType:=..sourceType,
              sourceText:=..sourceText,
              pinned:=..pinned,
            }}
          }

}

